import React, { useState } from "react";
import "./Experience.css";

function TimeLineItem(props) {
  return (
    <div
      className="timeline-item"
      onClick={() => {
        props.setActiveTitle(props.title);
        props.setActiveDescription(props.description);
        props.setActivePicture(props.picture);
      }}
    >
      <div className="experience-info">
        <h4>{props.title}</h4>
      </div>
    </div>
  );
}

export default function ExperienceScreen() {
  const [activeTitle, setActiveTitle] = useState("");
  const [activeDescription, setActiveDescription] = useState("");
  const [activePicture, setActivePicture] = useState("");

  return (
    <div className="screen-background">
      <div className="experience-background">
        <div className="timeline-background">
          <TimeLineItem
            title="Walla Walla High School"
            description="Nestled in the lush, green armpit of the Walla Walla Valley,
             Wa-Hi gave me my first exposure to so much, including the first two Computer Science
             classes that I'd ever taken. What it lacked in polish it more than made up for with
             country drives and those sweet, sweet onions."
             picture="../../assets/bighappy.png"
            setActiveTitle={setActiveTitle}
            setActiveDescription={setActiveDescription}
            setActivePicture={setActivePicture}
          />
          {/*
          <TimeLineItem
            title="another one"
            description="helo there"
            setActiveTitle={setActiveTitle}
            setActiveDescription={setActiveDescription}

          />
          */}
        </div>
        <div className="highlight-background">
          <h1>{activeTitle}</h1>
          <p>{activeDescription}</p>
          <img src={activePicture}>A Picture of the Valley</img>
        </div>
      </div>
    </div>
  );
}
