import React, { useState } from "react";
import "./Performances.css";
import Vid1 from "../../assets/Videos/Hoover.mov";
import Vid2 from "../../assets/Videos/Gounod.mov";
import Vid3 from "../../assets/Videos/Telemann.mov";
import Vid4 from "../../assets/Videos/Nielsen.mov";
import Vid5 from "../../assets/Videos/KE.mov";

function TimeLineItem(props) {
  return (
    <div
      className="timeline-item"
      onClick={() => {
        props.setActiveTitle(props.title);
        props.setActiveVid(props.vid);
      }}
    >
      <div className="performance-info">
        <h4>{props.title}</h4>
      </div>
    </div>
  );
}

export default function PerformancesScreen() {
  const [activeTitle, setActiveTitle] = useState("");
  const [activeVid, setActiveVid] = useState("");

  return (
    <div className="screen-background">
      <div className="experience-background">
        <div className="timeline-background">
          <TimeLineItem
            title="Katherine Hoover, Winter Spirits"
            description="It's a good'n!"
             vid={Vid1}
            setActiveTitle={setActiveTitle}
            setActiveVid={setActiveVid}
          />

          <TimeLineItem
            title="Gounod Concertino"
            description="It's a good'n too!"
             vid={Vid2}
            setActiveTitle={setActiveTitle}
            setActiveVid={setActiveVid}
          />

          <TimeLineItem
            title="Telemann Fantasia No. 2"
            description="Old, German."
             vid={Vid3}
            setActiveTitle={setActiveTitle}
            setActiveVid={setActiveVid}
          />  
          <TimeLineItem
            title="Nielsen Concerto, Mvmt. 1"
            description="HARD!"
             vid={Vid4}
            setActiveTitle={setActiveTitle}
            setActiveVid={setActiveVid}
          />  
          <TimeLineItem
            title="Karg-Elert Sonata Appasionata"
            description="HARD TOO!!!"
             vid={Vid5}
            setActiveTitle={setActiveTitle}
            setActiveVid={setActiveVid}
          />  
        </div>
        <div className="highlight-background">
          <h1>{activeTitle}</h1>
          <video src={activeVid} controls width="400">
            Sorry, your browser doesn't support embedded videos.
          </video>
        </div>
      </div>
    </div>
  );
}
